import 'package:monie_point/export.dart';

class BottomNavigation extends StatefulWidget {
  const BottomNavigation({Key? key}) : super(key: key);
  @override
  MyStatefulWidgetState createState() => MyStatefulWidgetState();
}

class MyStatefulWidgetState extends State<BottomNavigation> {
  // final Authentication _authentication = locator<Authentication>();
  // final NavigationService navigationService = locator<NavigationService>();

  late String counter;
  Widget getViewForIndex(int index) {
    if (kDebugMode) {
      print(index);
    }
    switch (index) {
      case 0:
        return const MyHomePage();
      case 1:
        return const Calculate();
      case 2:
        return const Shipment();
      case 3:
        return const Profile();
      default:
        return const MyHomePage();
    }
  }

  @override
  void initState() {
    super.initState();
  }

  @override
  Widget build(BuildContext context) {
    return Consumer<AppStateProvider>(builder: (context, model, _) {
      return Scaffold(
        backgroundColor: ImpactlyAppColors.backgroundColor,
        body: getViewForIndex(model.currentTabIndex),
        bottomNavigationBar: BottomNavigationBar(
          selectedFontSize: 12,
          unselectedFontSize: 10,
          // iconSize: 15,
          type: BottomNavigationBarType.fixed,
          onTap: (newTab) {
            // print(newTab);
            model.setCurrentTabTo(newTabIndex: newTab);
          },
          currentIndex: model.currentTabIndex,
          items: <BottomNavigationBarItem>[
            BottomNavigationBarItem(
              icon: Image.asset(
                "assets/icons/home.png",
                width: 23.0,
                color: model.currentTabIndex == 0
                    ? ImpactlyAppColors.leafGreen
                    : Colors.grey,
              ),
              label: 'Home',
            ),
            BottomNavigationBarItem(
              icon: SvgPicture.asset(
                "assets/icons/campaign.svg",
                width: 23.0,
                color: model.currentTabIndex == 1
                    ? ImpactlyAppColors.leafGreen
                    : Colors.grey,
              ),
              label: 'My Campaigns',
            ),
            BottomNavigationBarItem(
              icon: Image.asset(
                "assets/icons/notifications.png",
                width: 23.0,
                color: model.currentTabIndex == 2
                    ? ImpactlyAppColors.leafGreen
                    : Colors.grey,
              ),
              label: 'Notifications',
            ),
            BottomNavigationBarItem(
              icon: SvgPicture.asset(
                "assets/icons/profile.svg",
                width: 23.0,
                color: model.currentTabIndex == 3
                    ? ImpactlyAppColors.leafGreen
                    : Colors.grey,
              ),
              label: 'Profile',
            ),
          ],
          selectedItemColor: ImpactlyAppColors.leafGreen,
        ),
      );
    });
  }
}
